import React, { useEffect, useState } from "react";
import { getJSON, postJSON } from "../api";

export default function JobOpeningList() {
  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState("");
  const [requirements, setReq] = useState("");
  const [depts, setDepts] = useState([]);
  const [deptId, setDeptId] = useState("");

  useEffect(() => {
    (async () => {
      setDepts(await getJSON("/departments/"));
      setJobs(await getJSON("/jobs/"));
    })();
  }, []);

  async function add(e) {
    e.preventDefault();
    const payload = { title, requirements, department_id: deptId };
    await postJSON("/jobs/", payload);
    setTitle("");
    setReq("");
    setDeptId("");
    setJobs(await getJSON("/jobs/"));
  }

  return (
    <div>
      <form onSubmit={add} className="card">
        <input
          placeholder="Job title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <select value={deptId} onChange={(e) => setDeptId(e.target.value)}>
          <option value="">Select dept</option>
          {depts.map((d) => (
            <option key={d.department_id} value={d.department_id}>
              {d.name}
            </option>
          ))}
        </select>
        <input
          placeholder="Requirements"
          value={requirements}
          onChange={(e) => setReq(e.target.value)}
        />
        <button type="submit">Add Job</button>
      </form>
      <ul>
        {jobs.map((j) => (
          <li key={j.id}>
            {j.title} — {j.department?.name || "-"} — {j.status}
          </li>
        ))}
      </ul>
    </div>
  );
}
